mapper.select:
select t.id,t.args,t.asin,t.`level`,t.site,t.run_num,t.runing_num,t.run_completed,t.remaining,t.data_status,t.created_by,t.created_date from amazon_task t WHERE 1=1
and t.asin = #{asin}
and t.data_status = #{dataStatus}
order by t.`level` desc,t.created_date desc

select t1.calendar_date,ifnull(t.c,0) total from ( select tsc.calendar_date from tb_system_calendar tsc where tsc.calendar_date BETWEEN DATE_SUB(CURDATE(),INTERVAL 31 DAY) AND CURDATE() ) t1 LEFT JOIN ( select DATE_FORMAT(atr.end_time,'%Y-%m-%d') as end_time,count(1) c from amazon_task_run atr WHERE atr.end_time is not null
and atr.result_code=#{resultCode}
and atr.task_id=#{taskId}
group by DATE_FORMAT(atr.end_time,'%Y-%m-%d') ) t on t1.calendar_date=t.end_time order by t1.calendar_date

select ROUND(t1.success/t2.total*100,2) as success_rate,t1.success from ( select count(1) success from amazon_task_run atr where atr.result_code=2 and atr.task_id=#{taskId}
and atr.end_time > CURDATE()
and atr.end_time BETWEEN DATE_SUB(CURDATE(),INTERVAL 1 DAY) AND CURDATE()
and atr.end_time is not null
) t1, (select count(1) total from amazon_task_run atr where atr.task_id=#{taskId}
and atr.end_time > CURDATE()
and atr.end_time BETWEEN DATE_SUB(CURDATE(),INTERVAL 1 DAY) AND CURDATE()
and atr.end_time is not null
) t2
